script:
300
echo 'activating %{hostname}' azure='/usr/bin/azure' timeout=%{wa-activate-timeout} sleep=5 wait=0 while true; do state=`$azure vm show %{hostname} --json | python -c "import json; import sys; data=json.load(sys.stdin); print data['InstanceStatus']"` if [[ $state == StoppedVM ]] ; then $azure vm start %{hostname} fi if [[ $state == ReadyRole ]] ; then break else if [ "$wait" -lt "$timeout" ] ; then remaining=`expr $timeout - $wait` echo "Attempting to activate: $state, will retry for $remaining seconds" wait=`expr $wait + $sleep` sleep $sleep else echo "Activation timeout expired" exit 1 fi fi done
#!/bin/bash set -e
# vm exists? if ! [[ `/usr/bin/azure vm list` = *\ %{hostname}\ * ]]; then echo "vm does not exist" >&2 exit 1 fi
%{wait}
# raise error if provisioning failed if [[ $state == "ProvisioningFailed" ]] ; then echo "Activation failed with the following error: `/usr/bin/azure vm show %{hostname} --json | python -c \"import json; import sys; data=json.load(sys.stdin); print data['InstanceStateDetails']\"`" >&2 exit 1 fi
