config-rpm.script:
# install azure-mgmt-comput 
pip -q install --upgrade pip
info=`pip install azure-mgmt-compute ` || { echo $info >&2; exit 1; }
info=`pip install azure-mgmt-network ` || { echo $info >&2; exit 1; }
info=`pip install azure-mgmt-resource ` || { echo $info >&2; exit 1; }
info=`pip install azure-mgmt-storage ` || { echo $info >&2; exit 1; }
pip -q uninstall -y adal
info=`pip install adal ` || { echo $info >&2; exit 1; }
pip -q uninstall -y python-dateutil
info=`pip install python-dateutil` || { echo $info >&2; exit 1; }

