script:
#!/bin/sh
set -e echo "downloading release package" scp -o StrictHostKeyChecking=no %{localroot}/repo.conf root@%{ssh-host}:/etc/yum.repos.d/%{name}.repo ssh -o StrictHostKeyChecking=no root@%{ssh-host} yum -q -y install %{name}-release
echo "allowing kernel updates" ssh -o StrictHostKeyChecking=no root@%{ssh-host} "sed -i 's/^exclude=kernel\*/# exclude=kernel*/g' /etc/yum.conf"
