php55w php55w-opcache php55w-mbstring php55w-gd php55w-xml php55w-pear php55w-fpm drush-config /usr/sbin/sendmail /usr/sbin/postdrop
#!/bin/sh
file=${1:-%{drupal-settings-file}}
# remove existing trusted_host_patterns sed -i '/^#-----TRUSTED HOST PATTERNS-----#$/,$d' $file
# add trusted_host_patterns h=$(echo "`hostname` `hostname -I`" | sed "s/\./\\\./g") cat <<EOT >> $file #-----TRUSTED HOST PATTERNS-----# \$settings['trusted_host_patterns'] = array( '^`echo $h | sed "s/ /$',\n '^/g"`$', ); EOT
set -x
# download latest version mkdir -p %{download-dir} cd %{download-dir} wget -q https://ftp.drupal.org/files/projects/drupal-%{drupal-version}.tar.gz tar -zxpf drupal-%{drupal-version}.tar.gz rm -rf %{drupal-download-dir} mv -f drupal-%{drupal-version} %{drupal-download-dir}
# check for changes in default.settings.php if [ -f %{drupal-default-sites-dir}/settings.php ] && \ ! diff %{drupal-default-sites-dir}/default.settings.php \ %{drupal-download-dir}/sites/default/default.settings.php ; then echo "\ Error: The default.settings.php file has changed. You will need to complete \ the following steps manually:
1. Review changes to default.settings.php and manually update settings.php.
`/usr/bin/diff %{drupal-default-sites-dir}/default.settings.php %{drupal-download-dir}/sites/default/default.settings.php`
2. Copy %{drupal-download-dir}/sites/default/default.settings.php to \ %{drupal-default-sites-dir}/default.settings.php.
3. Run the script at %{install-dir}/post to complete the Drupal update. " >&2 exit 1 fi
# make drupal webroot mkdir -p %{drupal-web-dir}
# put site into maintenance mode, clear the cache in preparation for update cd %{drupal-web-dir} if drush config-get system.site name 2>/dev/null | \ grep -q "%{drupal-site-name}"; then drush state-set system.maintenance_mode 1 drush rebuild -q fi
# copy core and vendor folders to webroot for d in core vendor; do rm -rf %{drupal-web-dir}/$d cp -ar %{drupal-download-dir}/$d %{drupal-web-dir}/$d done
# copy top-level files to webroot find %{drupal-web-dir} -maxdepth 1 -type f -delete for f in `find %{drupal-download-dir} -maxdepth 1 -type f`; do cp -a $f %{drupal-web-dir} done
# configure drupal-web-dir dirs='modules profiles sites themes' for d in $dirs; do if ! [ -d %{drupal-web-dir}/$d ]; then cp -a %{drupal-download-dir}/$d %{drupal-web-dir} fi done
# create default settings.php if [ ! -f %{drupal-default-sites-dir}/settings.php ]; then cd %{drupal-default-sites-dir} cp -p default.settings.php settings.php fi
# set permissions on sites dir chown -R apache:apache %{drupal-web-dir}/sites chcon -R -t httpd_sys_content_rw_t %{drupal-web-dir}/sites
# create files dir and set permissions mkdir -p %{drupal-files-dir} chown -R %{drupal-user}:%{drupal-user} %{drupal-files-dir} chcon -R -t httpd_sys_content_rw_t %{drupal-files-dir}
# if site exists, update db, verify, and reset maintenance mode cd %{drupal-web-dir} if drush config-get system.site name 2>/dev/null | \ grep -q "%{drupal-site-name}"; then drush-error-wrapper updatedb -q -y drush-error-wrapper core-cron --uri=`hostname -f` drush-error-wrapper core-requirements drush-error-wrapper state-set system.maintenance_mode 0 fi
