#!/bin/sh # # Changes password for an existing user # # Usage: # mysql-change-user-password ADMIN_PASSWORD USER USER_PASSWORD
adminpass=$1 user=$2 userpass=$3
# update password mysql -p$adminpass <<EOF >/dev/null set password for '$user'@'localhost' = password('$userpass'); set password for '$user'@'%' = password('$userpass'); EOF
